import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

export default class PeopleList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      people: [],
      name: "",
      surname: "",
      age: "",
      female: false,
      male: true
    }
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    axios.get('https://venbest-test.herokuapp.com/')
      .then(res => {
        const people = res.data;
        this.setState({ people });
      })
  }

  handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <React.Fragment>
        <label>
          Имя:
          <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
          </label>
          <label>
          Фамилия:
          <input type="text" name="surname" value={this.state.surname} onChange={this.handleChange} />
          </label>
          <label>
          Возраст:
          <input type="number" name="age" value={this.state.age} onChange={this.handleChange} />
          </label>
          <label>
            Пол:
            <input type="checkbox" name="female" checked={this.state.female} onChange={this.handleChange} />
            <input type="checkbox" name="male" checked={this.state.male} onChange={this.handleChange} />
        </label>
        <ul>
          {
            this.state.people
            .filter(person => !this.state.name || person.name == this.state.name)
            .filter(person => !this.state.lastname || person.lastname == this.state.lastname)
            .filter(person => !this.state.sex || person.sex == this.state.sex)
            .filter(person => !this.state.age || person.age == this.state.age)
            .map((person, index) =>
              <li key={index}>{person.name} {person.lastname} {person.age} {person.sex}</li>
            )
          }
        </ul>
      </React.Fragment>
    )
  }
}
