import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

export default class PeopleList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      people: [],
      value: "",
      surname: "",
      name: "",
      age: ""
    }
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    axios.get('https://venbest-test.herokuapp.com/')
      .then(res => {
        const people = res.data;
        this.setState({ people });
      })
  }

  handleChange(event) {
    this.setState({value: event.target.value});
    console.log("Ololo",event.target);
  }

  render() {
    return (
      <React.Fragment>
        <label>
          Имя:
          <input type="text" name="name" value={this.state.isName} onChange={this.handleChange} />
          </label>
          <label>
          Фамилия:
          <input type="text" name="surname" value={this.state.isSurname} onChange={this.handleChange} />
          </label>
          <label>
          Возраст:
          <input type="number" name="age" value={this.state.isAge} onChange={this.handleChange} />
          </label>
          <label>
            Пол:
            <input type="checkbox" name="f" checked={this.state.female} onChange={this.handleChange} />
            <input type="checkbox" name="m" checked={this.state.male} onChange={this.handleChange} />
        </label>
        <ul>
          {
            this.state.people.map((person, index) =>
              <li key={index}>{person.name} {person.lastname} {person.age} {person.sex}</li>)
          }
        </ul>
      </React.Fragment>
    )
  }
}
